/**
 初始化籍贯省份,市悬方法
 provinceId 籍贯省份下拉id
 cityId    市悬下拉id
*/

function initProvincecity(provinceId,cityId){
   var obj=document.getElementById(provinceId);
   for(i = 0; i < provinceInit.length; i ++){
   obj.options.add(new Option(provinceInit[i],provinceInit[i]));
  }
   var  objCity=document.getElementById(cityId);

for(i = 0; i < cityInit.length; i ++){
objCity.options.add(new Option(cityInit[i],cityInit[i]));
 }


}






/**
 根据籍贯省份,显示相对应的市悬方法
 provinceId 籍贯省份下拉id
 cityId    市悬下拉id
*/




function provinceCityChange(provinceId,cityId){
 var obj=document.getElementById(provinceId);
 var val=obj.options[obj.selectedIndex].value;

 var objCity=document.getElementById(cityId);
for(i = 0; i < allCityArr.length; i ++){
if(val == allCityArr[i][0]){
objCity.options.length = 0;
for(j = 1 ; j < allCityArr[i].length; j ++){
objCity.options.add(new Option(allCityArr[i][j],allCityArr[i][j]));
//objCity.options.add(new Option("111","222222"));
//objCity.options.add(new Option("111,"22"));
}
//obj.options[0] = null;
//obj.click;
break;
}
}
}




